INSERT INTO topics (topic_id, lesson_id, topic_title, pdf_path, presentation_content) VALUES
(1, 1, 'Definition of reading', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Definition of reading","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Reading is an active process of making meaning from written text. It involves decoding symbols, connecting ideas, and using prior knowledge. Good readers set a purpose, predict, and monitor their understanding."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(2, 1, 'Definition of writing', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Definition of writing","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Writing is the process of producing text to communicate ideas. It involves planning, drafting, revising, and editing. Writing helps you clarify thinking and share your perspective with others."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(3, 1, 'Relationship between reading and writing', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Relationship between reading and writing","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Reading and writing are connected: reading builds vocabulary and models for writing; writing deepens comprehension and critical thinking. Practice both to strengthen your literacy."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(4, 1, 'Reading as a process (pre-reading, while-reading, post-reading)', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Reading as a process (pre-reading, while-reading, post-reading)","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Pre-reading: preview, set purpose, activate prior knowledge. While-reading: monitor understanding, make inferences, note key ideas. Post-reading: summarize, reflect, connect to other texts or experiences."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(5, 1, 'Writing as a process (prewriting, drafting, revising, editing, publishing)', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Writing as a process (prewriting, drafting, revising, editing, publishing)","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Prewriting: brainstorm and plan. Drafting: get ideas on the page. Revising: improve content and structure. Editing: fix grammar and style. Publishing: share the final work."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(6, 1, 'Importance of critical reading in academic life', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Importance of critical reading in academic life","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Critical reading helps you evaluate arguments, detect bias, and use evidence. In academic life you must distinguish fact from opinion and build your own reasoned positions."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(7, 1, 'Reading and writing across disciplines', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Reading and writing across disciplines","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Different subjects use different text types and conventions. Adapt your reading and writing strategies to each discipline while keeping a critical, evidence-based approach."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(8, 2, 'Narrative text', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Narrative text","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Narrative texts tell a story with characters, setting, and  plot. They often follow a sequence of events and may include dialogue and description. Identify the main events and the narrator''s perspective."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(9, 2, 'Expository text', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Expository text","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Expository texts explain or inform. They use facts, examples, and logical organization (e.g., cause-effect, compare-contrast). Look for the main idea and supporting details."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(10, 2, 'Argumentative text', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Argumentative text","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Argumentative texts take a position and support it with reasons and evidence. Identify the claim, reasons, evidence, and any counterarguments. Evaluate the strength of the argument."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(11, 2, 'Persuasive text', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Persuasive text","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Persuasive texts aim to convince the reader. They use rhetorical appeals (ethos, pathos, logos) and often call for action. Analyze the techniques used and the reliability of the message."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(12, 2, 'Description as a pattern of development', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Description as a pattern of development","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Description presents sensory and specific details to create a clear picture. Writers use precise language and spatial or logical order. Notice how details support the main impression."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(13, 2, 'Definition as a pattern of development', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Definition as a pattern of development","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Definition explains what something means. It may give a formal definition, examples, or extended explanation. Use it to clarify key terms in your reading and writing."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(14, 2, 'Classification as a pattern of development', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Classification as a pattern of development","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Classification sorts ideas or things into categories. It helps organize information and show relationships. Identify the categories and the basis for grouping."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(15, 2, 'Comparison and contrast', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Comparison and contrast","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Comparison shows similarities; contrast shows differences. This pattern helps analyze two or more subjects. Look for signal words and the criteria used for comparison."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(16, 2, 'Cause and effect', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Cause and effect","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Cause-and-effect text explains why something happens or what results from an action. Identify causes, effects, and whether the link is direct or complex."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(17, 2, 'Problem and solution', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Problem and solution","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Problem-solution text presents a problem and one or more solutions. Evaluate whether the solution is feasible and well supported by evidence."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(18, 2, 'Identifying text structures and analyzing how ideas are organized', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Identifying text structures and analyzing how ideas are organized","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Look at headings, signal words, and how ideas are ordered. Identifying structure helps you predict content, take notes, and summarize effectively."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(19, 3, 'Determining explicit and implicit information', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Determining explicit and implicit information","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Explicit information is stated directly. Implicit information is suggested or implied; you infer it from context and logic. Both matter for full comprehension."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(20, 3, 'Evaluating claims and evidence', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Evaluating claims and evidence","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"A claim is an assertion that needs support. Evidence includes facts, examples, and expert views. Evaluate whether the evidence is relevant, sufficient, and reliable."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(21, 3, 'Distinguishing facts from opinions', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Distinguishing facts from opinions","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Facts can be verified; opinions are judgments or beliefs. In academic writing, support opinions with evidence and distinguish between the two clearly."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(22, 3, 'Detecting bias and prejudice', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Detecting bias and prejudice","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Bias is a tendency to favor one view; prejudice is a preconceived judgment. Detect them by checking word choice, what is included or omitted, and the source''s purpose."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(23, 3, 'Identifying author''s purpose and tone', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Identifying author''s purpose and tone","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Purpose is why the author writes (inform, persuade, entertain). Tone is the attitude (formal, ironic, urgent). Identifying both helps you interpret and respond to the text."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(24, 3, 'Making inferences and conclusions', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Making inferences and conclusions","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Inferences are logical steps from evidence to an unstated idea. Conclusions sum up or decide based on evidence. Make sure your inferences are supported by the text."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(25, 4, 'Analyzing a text critically', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Analyzing a text critically","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Analyze by identifying the main claim, evidence, and assumptions. Consider the context, audience, and possible gaps or biases. Then form your own reasoned response."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(26, 4, 'Expressing agreement or disagreement', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Expressing agreement or disagreement","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"State clearly whether you agree or disagree (or partly agree). Support your position with reasons and evidence from the text and your knowledge."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(27, 4, 'Supporting opinions with evidence', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Supporting opinions with evidence","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Use examples, quotes, and data from the text or other sources. Explain how each piece of evidence supports your opinion. Avoid unsupported generalizations."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(28, 4, 'Using appropriate tone and academic language', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Using appropriate tone and academic language","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Use a formal, respectful tone. Choose precise vocabulary and avoid slang. Acknowledge other views fairly and cite sources appropriately."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(29, 4, 'Organizing ideas logically', '/uploads/Reading and Writing Skills/module1.pdf', '[{"type":"title","title":"Organizing ideas logically","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Order ideas so they build clearly: introduction with thesis, body paragraphs with one main idea each, and a conclusion that reinforces your response. Use transitions."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(30, 5, 'Essay writing (expository, argumentative)', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Essay writing (expository, argumentative)","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Expository essays explain; argumentative essays take a position and argue for it. Both need a clear thesis, organized body, and evidence. Follow the assignment''s requirements."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(31, 5, 'Research-based writing', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Research-based writing","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Research-based writing uses sources to support your claims. Find reliable sources, integrate them (summary, paraphrase, quote), and cite them correctly."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(32, 5, 'Formal tone and objective language', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Formal tone and objective language","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Academic writing is formal and objective: avoid \\"I\\" when not needed, avoid emotional language, and focus on evidence and logic rather than personal feeling."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(33, 5, 'Cohesion and coherence in writing', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Cohesion and coherence in writing","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Coherence means ideas flow logically; cohesion means sentences and paragraphs are linked (transitions, repetition, reference). Both make your writing easy to follow."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(34, 5, 'Proper paragraph development', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Proper paragraph development","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"A strong paragraph has one main idea, a topic sentence, supporting sentences with details or examples, and sometimes a concluding or transition sentence."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(35, 6, 'What is a thesis statement', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"What is a thesis statement","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"A thesis statement is one or two sentences that state your main argument or claim. It guides your paper and tells the reader what to expect. It should be specific and debatable."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(36, 6, 'Characteristics of a strong thesis statement', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Characteristics of a strong thesis statement","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"A strong thesis is clear, specific, arguable (not a fact), and supportable with evidence. It often appears at the end of the introduction."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(37, 6, 'Steps in formulating a thesis statement', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Steps in formulating a thesis statement","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Choose a topic, narrow it, ask a question, and draft an answer that can be argued. Revise until the thesis is clear and focused."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(38, 6, 'Creating an outline', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Creating an outline","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"List your main points and subpoints in order. Use numbers or letters. An outline keeps your logic clear and helps you avoid missing or repeating ideas."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(39, 6, 'Organizing main ideas and supporting details', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Organizing main ideas and supporting details","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Put your strongest points where they have the most impact. Each main idea should have supporting details or evidence. Keep a clear hierarchy: main idea ΓåÆ support."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(40, 7, 'Difference between summary, paraphrase, and quotation', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Difference between summary, paraphrase, and quotation","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"A summary shortens the main ideas in your own words. A paraphrase restates one part in your own words. A quotation uses the source''s exact words in quotation marks."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(41, 7, 'Techniques in summarizing texts', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Techniques in summarizing texts","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Identify the main idea and key supporting points. Leave out minor details and examples. Use your own words and keep the summary shorter than the original."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(42, 7, 'Avoiding plagiarism', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Avoiding plagiarism","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Cite every source you use. Paraphrase and summarize in your own words; put direct quotes in quotation marks. When in doubt, cite."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(43, 7, 'Proper citation of sources', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Proper citation of sources","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Use the style required (e.g., APA, MLA): in-text citations and a reference list or works cited. Be consistent and include all needed information."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(44, 7, 'Using signal phrases', '/uploads/Reading and Writing Skills/module2.pdf', '[{"type":"title","title":"Using signal phrases","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Signal phrases introduce sources: \\"According to X,\\" \\"Y argues that,\\" \\"As Z states.\\" They help integrate sources smoothly and show whose idea you are using."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(45, 8, 'Taking a clear stand on an issue', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Taking a clear stand on an issue","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"A position paper states your stance on an issue clearly in the introduction. The rest of the paper supports that position with reasons and evidence."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(46, 8, 'Supporting arguments with evidence', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Supporting arguments with evidence","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Support each reason with facts, examples, statistics, or expert views. Explain how the evidence supports your claim. One strong piece of evidence per point is a good minimum."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(47, 8, 'Counterarguments and refutation', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Counterarguments and refutation","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Address opposing views fairly. Present the counterargument, then refute it with reasoning and evidence. This strengthens your own position."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(48, 8, 'Logical reasoning', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Logical reasoning","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Use clear logic: avoid fallacies, connect premises to conclusions, and make sure your evidence actually supports your claims. Inductive and deductive reasoning can both be used."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(49, 8, 'Proper formatting of position papers', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Proper formatting of position papers","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Follow a clear structure: introduction with thesis, body paragraphs (one main reason per paragraph), counterargument and refutation, conclusion. Use formal style and citations."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(50, 9, 'Primary and secondary sources', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Primary and secondary sources","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Primary sources are original (e.g., studies, speeches, documents). Secondary sources discuss or analyze primary sources (e.g., reviews, textbooks). Use both when appropriate."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(51, 9, 'Evaluating reliability of sources', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Evaluating reliability of sources","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Check the author''s expertise, the publication, the date, and whether the source is peer-reviewed or from a reputable organization. Avoid unreliable or biased sources."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(52, 9, 'Academic databases and credible websites', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Academic databases and credible websites","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Use library databases and credible websites (e.g., .edu, .gov, recognized journals). Avoid random websites and social media as main sources unless they are clearly reliable."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(53, 9, 'Ethical use of information', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Ethical use of information","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Use information ethically: cite sources, do not distort or misquote, and respect copyright. Acknowledge the work of others and contribute your own analysis."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(54, 9, 'Documentation styles (APA/MLA basic format)', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Documentation styles (APA/MLA basic format)","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"APA and MLA are common. APA is often used in social sciences; MLA in humanities. Follow one style consistently for in-text citations and the reference list."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(55, 10, 'Clarity and conciseness', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Clarity and conciseness","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Write clearly: use precise words and straightforward sentences. Be concise: cut unnecessary words and repetition. Every sentence should add something."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(56, 10, 'Unity and coherence', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Unity and coherence","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Unity means each paragraph focuses on one main idea. Coherence means ideas connect logically and transitions guide the reader. Check that nothing is off-topic or choppy."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(57, 10, 'Logical organization', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Logical organization","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Organize your paper so ideas build in a clear order: introduction, body (e.g., by time, importance, or category), and conclusion. Use headings if helpful."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(58, 10, 'Proper grammar and mechanics', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Proper grammar and mechanics","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Use correct grammar, spelling, and punctuation. Proofread carefully. Errors can distract readers and weaken your credibility."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(59, 10, 'Formal tone and vocabulary', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Formal tone and vocabulary","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Use formal tone and vocabulary appropriate for academic writing. Avoid slang, contractions (if required), and vague language. Be precise and respectful."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
(60, 10, 'Audience awareness', '/uploads/Reading and Writing Skills/module3.pdf', '[{"type":"title","title":"Audience awareness","body":"This topic is part of Reading and Writing Skills. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Consider who will read your work: their knowledge level, expectations, and purpose. Adapt your tone, examples, and level of detail to your audience."},{"type":"content","title":"Why it matters","body":"Applying these ideas will help you read critically and write clearly in academic and real-world contexts."},{"type":"content","title":"Summary","body":"Review the key ideas and try applying them in your next reading or writing task."}]'),
