ON DUPLICATE KEY UPDATE subject_name = VALUES(subject_name);

-- Lessons: R&W 1–10 (subject 1), Oral Comm 11–13 (subject 2), Creative Non-Fiction 14–16 (subject 4)
ON DUPLICATE KEY UPDATE lesson_title = VALUES(lesson_title);

-- Topics with presentation_content (one JSON slide array per topic)
-- Oral Communication (subject 2): lessons 11–13 → module1, module2, module3
(61, 11, 'Definition and nature of communication', '/uploads/Oral Communication/module1.pdf', '[{"type":"title","title":"Definition and nature of communication","body":"This topic is part of Oral Communication. Work through each slide to understand the key ideas."},{"type":"content","title":"Key ideas","body":"Communication is the process of sharing meaning through symbols. It involves a sender, message, channel, receiver, and feedback. Communication can be verbal or nonverbal."},{"type":"content","title":"Summary","body":"Review the key ideas and apply them in your next speaking or listening task."}]'),
(62, 11, 'Elements of communication', '/uploads/Oral Communication/module1.pdf', '[{"type":"title","title":"Elements of communication","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"The main elements are sender, receiver, message, channel, feedback, context, and noise. Each element affects how meaning is created and understood."},{"type":"content","title":"Summary","body":"Review the elements and apply them when preparing a speech or analyzing a conversation."}]'),
(63, 11, 'Models of communication', '/uploads/Oral Communication/module1.pdf', '[{"type":"title","title":"Models of communication","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"Models (linear, interactive, transactional) show how communication works. Linear: one-way; interactive: adds feedback; transactional: simultaneous sending and receiving."},{"type":"content","title":"Summary","body":"Use these models to understand and improve your own communication."}]'),
(64, 11, 'Verbal and nonverbal communication', '/uploads/Oral Communication/module1.pdf', '[{"type":"title","title":"Verbal and nonverbal communication","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"Verbal communication uses words; nonverbal includes gestures, tone, posture, and eye contact. Both work together to convey meaning and emotion."},{"type":"content","title":"Summary","body":"Pay attention to both verbal and nonverbal cues in speaking and listening."}]'),
(65, 12, 'Functions of communication (regulation, social interaction, motivation)', '/uploads/Oral Communication/module2.pdf', '[{"type":"title","title":"Functions of communication","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"Communication serves to control or regulate behavior, to allow social interaction, and to motivate. Identify the function in different speech situations."},{"type":"content","title":"Summary","body":"Apply the functions when preparing or analyzing speeches."}]'),
(66, 12, 'Types of speech (informative, persuasive, entertainment)', '/uploads/Oral Communication/module2.pdf', '[{"type":"title","title":"Types of speech","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"Informative speech teaches; persuasive speech convinces; entertainment speech amuses or engages. Each type has different goals and strategies."},{"type":"content","title":"Summary","body":"Choose the right type and structure for your purpose and audience."}]'),
(67, 12, 'Speech preparation and outlining', '/uploads/Oral Communication/module2.pdf', '[{"type":"title","title":"Speech preparation and outlining","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"Prepare by choosing a topic, researching, and outlining (introduction, body, conclusion). An outline keeps your speech organized and clear."},{"type":"content","title":"Summary","body":"Use outlining in every formal speech you prepare."}]'),
(68, 12, 'Delivery and use of visual aids', '/uploads/Oral Communication/module2.pdf', '[{"type":"title","title":"Delivery and use of visual aids","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"Effective delivery uses clear voice, eye contact, and gestures. Visual aids (slides, props) should support, not replace, your message."},{"type":"content","title":"Summary","body":"Practice delivery and use visual aids to strengthen your speech."}]'),
(69, 13, 'Speech context (intrapersonal, interpersonal, public)', '/uploads/Oral Communication/module3.pdf', '[{"type":"title","title":"Speech context","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"Intrapersonal is within oneself; interpersonal is between people; public is to an audience. Context affects style, formality, and content."},{"type":"content","title":"Summary","body":"Adapt your communication to the context."}]'),
(70, 13, 'Speech style (frozen to intimate)', '/uploads/Oral Communication/module3.pdf', '[{"type":"title","title":"Speech style","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"Speech styles range from frozen (fixed) to intimate (personal). Choose the style that fits the occasion and relationship with the audience."},{"type":"content","title":"Summary","body":"Match your style to the situation and audience."}]'),
(71, 13, 'Principles of effective speech delivery', '/uploads/Oral Communication/module3.pdf', '[{"type":"title","title":"Principles of effective speech delivery","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"Be clear, confident, and audience-centered. Manage nervousness, use pauses, and respond to feedback. Practice and prepare."},{"type":"content","title":"Summary","body":"Apply these principles in every speech you give."}]'),
(72, 13, 'Evaluating speeches', '/uploads/Oral Communication/module3.pdf', '[{"type":"title","title":"Evaluating speeches","body":"This topic is part of Oral Communication."},{"type":"content","title":"Key ideas","body":"Evaluate content, organization, delivery, and audience impact. Give and receive feedback constructively to improve."},{"type":"content","title":"Summary","body":"Use evaluation criteria to improve your own and others'' speeches."}]'),
-- Creative Non-Fiction (subject 3): lessons 14–16 → module1, module2, module3
(73, 14, 'Definition of creative non-fiction', '/uploads/Creative Non-Fiction/module1.pdf', '[{"type":"title","title":"Definition of creative non-fiction","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Creative non-fiction uses literary techniques to tell true stories. It combines factual accuracy with narrative craft (scene, dialogue, reflection)."},{"type":"content","title":"Summary","body":"Review the definition and read examples of the genre."}]'),
(74, 14, 'Difference from fiction and journalism', '/uploads/Creative Non-Fiction/module1.pdf', '[{"type":"title","title":"Difference from fiction and journalism","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Unlike fiction, it is based on fact; unlike straight journalism, it uses narrative and stylistic choices. Truth and craft work together."},{"type":"content","title":"Summary","body":"Understand the boundaries and possibilities of the genre."}]'),
(75, 14, 'Literary elements in creative non-fiction', '/uploads/Creative Non-Fiction/module1.pdf', '[{"type":"title","title":"Literary elements in creative non-fiction","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Plot, character, setting, point of view, and theme are used as in fiction but grounded in real events and people."},{"type":"content","title":"Summary","body":"Apply literary elements when reading and writing creative non-fiction."}]'),
(76, 14, 'Ethics and truth in the genre', '/uploads/Creative Non-Fiction/module1.pdf', '[{"type":"title","title":"Ethics and truth in creative non-fiction","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Writers must be accurate and fair. Composite characters or compressed time should be acknowledged. Respect subjects and readers."},{"type":"content","title":"Summary","body":"Write with integrity and transparency."}]'),
(77, 15, 'Scene and summary', '/uploads/Creative Non-Fiction/module2.pdf', '[{"type":"title","title":"Scene and summary","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Scene shows moments in detail (like a film); summary condenses time. Use both to control pace and emphasis in your narrative."},{"type":"content","title":"Summary","body":"Balance scene and summary in your writing."}]'),
(78, 15, 'Dialogue and voice', '/uploads/Creative Non-Fiction/module2.pdf', '[{"type":"title","title":"Dialogue and voice","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Dialogue reveals character and advances the story. Voice is the distinct personality of the narrator. Both must feel authentic."},{"type":"content","title":"Summary","body":"Use dialogue and voice to bring true stories to life."}]'),
(79, 15, 'Point of view and persona', '/uploads/Creative Non-Fiction/module2.pdf', '[{"type":"title","title":"Point of view and persona","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"First person is common; third person can create distance. Persona is the narrator''s role and attitude. Choose what best serves the story."},{"type":"content","title":"Summary","body":"Choose point of view and persona deliberately."}]'),
(80, 15, 'Sensory detail and imagery', '/uploads/Creative Non-Fiction/module2.pdf', '[{"type":"title","title":"Sensory detail and imagery","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Use specific sensory details (sight, sound, touch, smell, taste) to create vivid, memorable scenes. Show rather than only tell."},{"type":"content","title":"Summary","body":"Revise for concrete, sensory language."}]'),
(81, 16, 'Drafting creative non-fiction', '/uploads/Creative Non-Fiction/module3.pdf', '[{"type":"title","title":"Drafting creative non-fiction","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Start with material you know or can research. Draft freely first; structure and polish later. Use scenes, reflection, and research."},{"type":"content","title":"Summary","body":"Get a full draft before heavy editing."}]'),
(82, 16, 'Revision and structure', '/uploads/Creative Non-Fiction/module3.pdf', '[{"type":"title","title":"Revision and structure","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Revise for clarity, flow, and impact. Consider order of scenes, balance of summary and scene, and the arc of the piece."},{"type":"content","title":"Summary","body":"Revise in multiple passes for content and style."}]'),
(83, 16, 'Research and fact-checking', '/uploads/Creative Non-Fiction/module3.pdf', '[{"type":"title","title":"Research and fact-checking","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Verify facts, dates, and quotes. Use primary sources when possible. Note what you cannot verify and how you handle it."},{"type":"content","title":"Summary","body":"Ground your creative non-fiction in accurate research."}]'),
(84, 16, 'Publication and sharing your work', '/uploads/Creative Non-Fiction/module3.pdf', '[{"type":"title","title":"Publication and sharing your work","body":"This topic is part of Creative Non-Fiction."},{"type":"content","title":"Key ideas","body":"Consider audience and venue. Respect privacy and get permission when needed. Submit to journals, contests, or share in class or online."},{"type":"content","title":"Summary","body":"Prepare and share your work responsibly."}]')
ON DUPLICATE KEY UPDATE lesson_id = VALUES(lesson_id), topic_title = VALUES(topic_title), pdf_path = VALUES(pdf_path), presentation_content = VALUES(presentation_content);
